import React,{Component} from "react";
import axios from 'axios';
import StudentNavBar from '../Layout/StudentNavBar';
import Footer from '../Layout/footer';

export default class StudentLogin extends Component {
    constructor(props) {
      super(props);
      this.userLoginSubmit = this.userLoginSubmit.bind(this)
      this.handleClose = this.handleClose.bind(this)
      this.state = {
        student_id: "",
        pwd: "",
        token: "",
        open: false
      }
    }
    async userLoginSubmit(e) {
      e.preventDefault()
      const userData = {
        student_id: this.state.student_id,
        pwd: this.state.pwd
      }

 await axios.post("http://localhost:8070/student/login",userData)
      .then((res) => {
        this.setState({
          token: res.data.token
        }) 
        localStorage.setItem("Authorization", res.data.token)
           window.location = "/studentdashboard"
           alert('Loging Successfull');
      })
      .catch((err) => {
           console.log(err)
           this.setState({open: true})
            alert('loging unsucces',err);
      })
    }
  
    handleClose(reason) {
           if (reason === 'clickaway') {
           return;
      }
           this.setState({open: false})
   };
 
